    for (int j = 1; j <= i - 1; j++) {
      if (s[j-1] == '1') {
        set(A[i], j);
      } else {
        set(A[j], i);
      }
    }
  }
  for (int i = 0; i+1 <= N+1; i++) {
    set(dpL[i], i + 1);
    set(dpR[i + 1], i);
  }
  for (int d = 2; d <= N; d++) {
    for (int i = 0; i + d <= N+1; i++) {
      int j = i + d;
      ull tmp[32] = {};
      for (int k = 0; k < 32; k++) {
        tmp[k] = A[i][k] | A[j][k];
      }
      for (int k = 0; k < 32; k++) {
        tmp[k] &= dpL[i][k] & dpR[j][k];
      }
      // i < k < j
      if (exists(tmp, i + 1, j)) {
        set(dpL[i], j);
        set(dpR[j], i);
      }
    }
  }
  int ans = 0;
  for (int i = 1; i <= N; i++) {
