#define N 2010
ll n;
bool a[N][N];
ll sl[N],sr[N];
vector<ll> g[N];
bool ok;
P dfs(ll x,ll from){
    vector<P> v;
    for(auto y:g[x])if(y!=from){
	v.push_back(dfs(y,x));
    }
    if(v.size()==0)return make_pair(x,x);
    sort(v.begin(),v.end());
    for(int i=0;i<v.size()-1;i++){
	if(v[i].second>v[i+1].first)ok=0;
    }
    return make_pair(v[0].first,v.back().second);
}
bool used[N];
bool solve(ll s){
    for(int i=0;i<n;i++){
	g[i].clear();
	used[i]=0;
    }
    for(int i=s-1;i>=0;i--){
	if(used[i])continue;
	ll p=i,bef=-1;
	while(1){
	    used[p]=1;
	    if(~bef)g[p].push_back(bef);
	    bef=p;
	    if(a[s][p]){
		g[s].push_back(p);
		break;
	    }
	    if(sl[p]==-1)return 0;
	    p=sl[p];
	}
    }
    for(int i=s+1;i<n;i++){
	if(used[i])continue;
	ll p=i,bef=-1;
	while(1){
	    used[p]=1;
	    if(~bef)g[p].push_back(bef);
	    bef=p;
	    if(a[s][p]){
		g[s].push_back(p);
		break;
	    }
	    if(sr[p]==-1)return 0;
	    p=sr[p];
	}
    }
    ok=1;
    dfs(s,-1);
    return ok;
}
