      for(int j=0;j<i;j++){
	  char z;cin>>z;
	  a[i][j]=z-'0';
	  a[j][i]=a[i][j]^1;
      }
  }
  for(int i=0;i<n;i++){
      sl[i]=-1;
      for(int j=i-1;j>=0;j--){
	  if(a[i][j]==0){
	      sl[i]=j;
	      break;
	  }
      }
      sr[i]=-1;
      for(int j=i+1;j<n;j++){
	  if(a[i][j]==0){
	      sr[i]=j;
	      break;
	  }
      }
  }
  ll ans=0;
  for(int i=0;i<n;i++){
      ans+=solve(i);
      //cout<<i<<":"<<solve(i)<<endl;
      //cout<<sl[i]<<" "<<sr[i]<<endl;
  }
  cout<<ans<<endl;
  return 0;
}

