inline int read_char() {
    static char buf[1 << 16], *ptr = buf, *end_ptr = buf;
    if (ptr == end_ptr) {
        int len = fread(buf, 1, sizeof(buf), stdin);
        if (len <= 0) return EOF;
        ptr = buf;
        end_ptr = buf + len;
    }
    return *ptr++;
}
inline int readint(){
    int t=0;
    char c;
    bool negative=false;
    c=read_char();
    while(c<'0'&&c>'9'){
        if(c=='-')
            negative=true;
        c=read_char();
    }
    while(c>='0'&&c<='9'){
        t=(t<<3)+(t<<1)+c-'0';
        c=read_char();
    }
    if(negative)
        t=~(t-1); //negative
    return t;
}


