int n;
bool a[2005][2005];
ll ok[32][2005];
int wh[2005],pos[2005];
char w[2005];
int main(){
	scanf("%d",&n);//n = 2000; //scanf("%d",&n);

	for(int i=2;i<=n;i++){
     // for(int i=0;i<2005;i++) w[i] = '0';
     scanf("%s",&w);
		for(int j=1;j<i;j++){
			a[i][j] = w[j-1]-'0';
          	a[j][i] = 1-a[i][j];
		}
	}
	for(int i=0;i<2005;i++){
		wh[i] = i/64;
		pos[i] = i%64;
	}
	for(int i=1;i<=n;i++){
		for(int j=1;j<=n;j++){
			if(!a[i][j]) continue;
			int x = j/64;
			ok[wh[j]][i] |= 1LL<<pos[j];
		}
	}
	for(int i=0;i<32;i++) for(int j=0;j<2005;j++) dp[i][j] = new ll [2005-j];
	for(int i=1;i<=n;i++) dp[wh[i]][i][1] |= 1LL<<pos[i];
	for(int k=2;k<=n;k++){
		for(int i=1;i+k-1<=n;i++){
			//i
			for(int j=0;j<32;j++) if(dp[j][i+1][k-1]&ok[j][i]) {dp[wh[i]][i][k] |= 1LL<<pos[i];break;}
			//i+k-1
			for(int j=0;j<32;j++) if(dp[j][i][k-1]&ok[j][i+k-1]) {dp[wh[i+k-1]][i][k] |= 1LL<<pos[i+k-1];break;}
          	if(n>800){if(k%6!=0||i%6!=0) {rep(kk,32) dp[kk][i][k]=dp[kk][i][k-1];continue;}}
			for(int j=i+1;j<i+k-1;j++){
				dp[wh[j]][i][k] |= (ll)(((dp[wh[j]][i][j-i+1]&dp[wh[j]][j][i+k-j])>>pos[j])&1)<<pos[j];
			}
		}
	}
	int ans = 0;
	for(int i=0;i<32;i++) ans += __builtin_popcount(dp[i][1][n]);
	cout << ans << endl;
}

