void solve() {
	int i,j,k,l,r,x,y; string s;

	cin>>N;
	for(j=1;j<N;j++) {
		cin>>s;
		FOR(i,j) {
			A[j][i]=s[i]=='1';
			A[i][j]=s[i]=='0';
		}
	}

	FOR(x,N) winL[x][x]=winR[x][x]=winRL[x][x]=winRR[x][x]=1;
	for(l=2;l<=N;l++) {
		for(x=0;x+l<=N;x++) {
			y=x+l-1;
			for(i=x;i<y && dp[x][y]==0;i++) if(winL[x][i]&winR[y][i+1]) dp[x][y]=1;
