			bs=A[y] & dp[y] & winRR[x];
			if(bs.count()) winR[y][x]=winRR[x][y]=1;
		}
	}
	int ret=0;
	FOR(i,N) ret+=winR[i][0]&winL[i][N-1];

	cout<<ret<<endl;

