#include<cstdio>
#include<algorithm>
using namespace std;

#define ran 2022

int n;
char s[ran];
bool f[ran][ran];
int lef[ran], rig[ran];

int need_pull[ran][ran];
int cnp[ran];

int need_upd[ran][ran];
int cnu[ran];

int q[ran*ran], l, r;
bool inq[ran];

int main() {
	scanf("%d", &n);
	for(int i=2;i<=n;i++) {
		scanf("%s", s);
		for(int j=1;j<i;j++) {
			int x = s[j-1]-'0';
			if (x) {
				need_pull[i][cnp[i]++] = j;
				need_upd[j][cnu[j]++] = i;
			}
			else {
				need_pull[j][cnp[j]++] = i;
				need_upd[i][cnu[i]++] = j;
			}
			f[i][j] = x; f[j][i] = 1-x;
		}
	}

	l=r=0;
	for(int i=1;i<=n;i++) {
		if(i>1&&f[i][i-1]) lef[i] = i-1;
		else lef[i] = i;
		if(i<n&&f[i][i+1]) rig[i] = i+1;
		else rig[i] = i;

		q[r++] = i;
		inq[i] = true;
	}

	int lim = ran*ran-11;
	while(l!=r) {
		int cur = q[l++]; // cur = 1 [1,2]; nxt = 3 [3,3];
		if(l==lim) l=0;
		for(int i=0;i<cnp[cur];i++) {
			int nxt = need_pull[cur][i];
			//printf("%d %d\n", cur, nxt);

			if (nxt<cur && rig[nxt]>=lef[cur]-1) lef[cur] = min(lef[cur], lef[nxt]);
			if (cur<nxt && rig[cur]>=lef[nxt]-1) rig[cur] = max(rig[cur], rig[nxt]);
		}
		for(int i=0;i<cnu[cur];i++) {
			int nxt = need_upd[cur][i];
			bool upd = false;
			if(nxt<cur && rig[nxt] >= lef[cur]-1 && rig[nxt] < rig[cur]) {
				rig[nxt] = rig[cur];
				upd = true;
			}
			if(cur<nxt && rig[cur] >= lef[nxt]-1 && lef[nxt] > lef[cur]) {
				lef[nxt] = lef[cur];
				upd = true;
			}
			if(upd && !inq[nxt]) {
				q[r++] = nxt;
				if(r==lim) r=0;
				inq[nxt] = true;
			}
		}
		inq[cur] = false;
	}

	int ans=0;
	for(int i=1;i<=n;i++) {
		//printf("%d %d\n", lef[i], rig[i]);
		if(lef[i]==1 && rig[i]==n) {
			//printf("%d\n", i);
			ans++;
		}
	}
	printf("%d\n", ans);

	return 0;
}

