constexpr int N = 2000;
int dp1[N][N]; // left win
int dp2[N][N]; // right win
bool A[N][N];
int n;

int rec2(int a, int b);

// left win
int rec1(int a, int b){
  if(dp1[a][b] != -1) return dp1[a][b];
  repr(i, a+1, b+1){
    if(A[a][i] & rec1(i, b)){
      repr(j, a, i){
        if(rec1(a, j) & rec2(j+1, i)){
          return dp1[a][b] = 1;
        }
      }
    }
  }
  return dp1[a][b] = 0;
}

// right win
int rec2(int a, int b){
  if(dp2[a][b] != -1) return dp2[a][b];
  repr(i, a, b){
    if(A[b][i] & rec2(a, i)){
      repr(j, i, b){
        if(rec1(i, j) & rec2(j+1, b)){
          return dp2[a][b] = 1;
        }
      }
    }
  }
  return dp2[a][b] = 0;
}

