  memset(dp1, -1, sizeof(int) * N*N);
  memset(dp2, -1, sizeof(int) * N*N);
  rep(i, N) dp1[i][i] = 1;
  rep(i, N) dp2[i][i] = 1;

  int ans = 0;
  rep(i, n){
