#[derive(Clone, Debug)]
struct BitVector {
    n: usize,
    size: usize,
    data: Vec<u64>
}

impl BitVector {
    fn new(n: usize) -> Self {
        let size = (n+63)/64;
        BitVector {
            n: n,
            size: size,
            data: vec![0; size]
        }
    }

    fn get(&self, i: usize) -> bool {
        self.data[i/64]>>(i%64) & 1 != 0
    }

    fn set(&mut self, i: usize) {
        self.data[i/64] |= 1<<(i%64);
    }

    fn count(&self) -> usize {
        let mut cnt = 0;
        for i in 0..self.size {
            cnt += self.data[i].count_ones();
        }
        cnt as usize
    }
}

impl<'a> std::ops::Shl<usize> for &'a BitVector {
    type Output = BitVector;

    fn shl(self, amount: usize) -> Self::Output {
        let mut new_vector = BitVector::new(self.n);

        let diff = amount / 64;
        let diff_mod = amount % 64;
        if diff_mod == 0 {
            for i in 0..self.size-diff {
                new_vector.data[i+diff] = self.data[i];
            }
        } else {
            let lower_mask = (1<<(64-diff_mod))-1;
            let upper_mask = ((1<<diff_mod)-1)<<(64-diff_mod);
            for i in 0..self.size-diff {
                new_vector.data[i+diff] |= (self.data[i] & lower_mask)<<diff_mod;
                if i+diff+1 < self.size {
                    new_vector.data[i+diff+1] |= (self.data[i] & upper_mask)>>(64-diff_mod);
                }
            }
        }
        new_vector
    }
}


impl<'a> std::ops::Shr<usize> for &'a BitVector {
    type Output = BitVector;

    fn shr(self, amount: usize) -> Self::Output {
        let mut new_vector = BitVector::new(self.n);

        let diff = amount / 64;
        let diff_mod = amount % 64;
        if diff_mod == 0 {
            for i in diff..self.size {
                new_vector.data[i-diff] = self.data[i];
            }
        } else {
            let lower_mask = (1<<diff_mod)-1;
            let upper_mask = ((1<<(64-diff_mod))-1)<<diff_mod;
            for i in diff..self.size {
                new_vector.data[i-diff] |= (self.data[i] & upper_mask)>>diff_mod;
                if i > diff {
                    new_vector.data[i-diff-1] |= (self.data[i] & lower_mask)<<(64-diff_mod);
                }
            }
        }
        new_vector
    }
}

impl<'a> std::ops::BitAnd for &'a BitVector {
    type Output = BitVector;

    fn bitand(self, other: Self) -> Self::Output {
        let mut output = BitVector::new(self.n);
        for i in 0..self.size {
            output.data[i] = self.data[i] & other.data[i];
        }
        output
    }
}

impl std::ops::BitAndAssign for BitVector {
    fn bitand_assign(&mut self, rhs: Self) {
        for i in 0..min(self.size, rhs.size) {
            self.data[i] &= rhs.data[i];
        }
    }
}

impl<'a> std::ops::BitOr for &'a BitVector {
    type Output = BitVector;

    fn bitor(self, other: Self) -> Self::Output {
        let mut output = BitVector::new(self.n);
        for i in 0..self.size {
            output.data[i] = self.data[i] | other.data[i];
        }
        output
    }
}

impl std::ops::BitOrAssign for BitVector {
    fn bitor_assign(&mut self, rhs: Self) {
        for i in 0..min(self.size, rhs.size) {
            self.data[i] |= rhs.data[i];
        }
    }
}

impl std::ops::BitXorAssign for BitVector {
    fn bitxor_assign(&mut self, rhs: Self) {
        for i in 0..min(self.size, rhs.size) {
            self.data[i] ^= rhs.data[i];
        }
    }
}

