fn main() {
    input! {
        n: usize,
        table: [chars; n-1]
    };

    // 1 to n, 0 and n+1 are sentinels
    let mut wintable = vec![BitVector::new(2048); n+2];
    for i in 2..n+1 {
        for j in 1..i {
            if table[i-2][j-1] == '1' {
                // i wins j
                wintable[i].set(j);
            } else {
                wintable[j].set(i);
            }
        }
    }

    let mut dp_left = vec![BitVector::new(2048); n+2];
    let mut dp_right = vec![BitVector::new(2048); n+2];

    let mut dp_left_rev = vec![BitVector::new(2048); n+2];
    let mut dp_right_rev = vec![BitVector::new(2048); n+2];

    for i in 1..n+1 {
        dp_left[i].set(i);
        dp_right[i].set(i);
        dp_left_rev[i].set(i);
        dp_right_rev[i].set(i);
    }
    for l in 1..n {
        for i in 1..n+1 {
            if i > l {
                let lj = i-l;
                let mut ok = false;
                ok |= (&(&(&dp_left[i]>>1) & &dp_left_rev[lj]) & &wintable[i]).count() >= 1;
                ok |= wintable[i].get(lj) && (&dp_left[i] & &(&dp_right[lj]<<1)).count() >= 1;
                if ok {
                    dp_left[i].set(lj);
                    dp_left_rev[lj].set(i);
                }
                // debug!(i, lj, ok);
            }
            if i+l <= n {
                let rj = i+l;
                let mut ok1 = false;
                let mut ok2 = false;
                ok1 |= (&(&(&dp_right[i]<<1) & &dp_right_rev[rj]) & &wintable[i]).count() >= 1;
                ok2 |= wintable[i].get(rj) && (&dp_right[i] & &(&dp_left[rj]>>1)).count() >= 1;
                let ok = ok1 | ok2;
                if ok {
                    dp_right[i].set(rj);
                    dp_right_rev[rj].set(i);
                }
                // debug!(i, rj, ok1, ok2);
            }
        }
    }


    let mut count = 0;
    for i in 1..n+1 {
        if dp_left[i].get(1) && dp_right[i].get(n) {
            count += 1;
            // debug!(i);
        }
    }
    println!("{}", count);
}

