bool result[2010][2010];

map<pair<long, long>, unordered_set<long>> winners;
map<pair<long, long>, bool> calculated;

void calc(long from, long to) {
  pair<long, long> query = make_pair(from, to);
  if (calculated[query]) return;
  calculated[query] = true;
  if (from + 1 == to) {
    winners[query].insert(from);
    return;
  }
  for (long i = 1; i < to - from; i++) {
    calc(from, from + i);
    calc(from + i, to);
    auto set_foo = winners[make_pair(from, from + i)];
    auto set_bar = winners[make_pair(from + i, to)];
    for (auto foo: set_foo) for (auto bar: set_bar) {
      if (result[foo][bar]) winners[query].insert(foo);
      winners[query].insert(bar);
    }
  }
}

int main(void) {
  cout.precision(12);

  long N;
  cin >> N;


  for (long i = 2; i <= N; i++) {
    string s;
    cin >> s;
    for (long j = 0; j < i-1; j++) {
      result[i-1][j] = s[j] == '1';
      result[j][i-1] = s[j] == '0';
    }
  }

  calc(0, N);
  for (auto it: winners) {
    cout << "(" << it.first.first << ", " << it.first.second << "): ";
    for (auto it2: it.second) cout << it2 << " ";
    cout << endl;
  }
  cout << winners[make_pair(0, N)].size() << endl;

  return 0;
}

