  V< bitset<2000> > a(n);
  for (int i = 1; i < n; ++i) {
    string s; cin >> s;
    for (int j = 0; j < i; ++j) {
      a[i][j] = s[j] == '1';
      a[j][i] = s[j] == '0';
    }
  }
  V< bitset<2000> > dp(n);
  for (int i = 0; i < n; ++i) {
    dp[i][i] = true;
    if (i) dp[i - 1][i] = a[i][i - 1];
    if (i + 1 < n) dp[i + 1][i] = a[i][i + 1];
  }
  for (int w = 2; w < n; ++w) {
    for (int i = 0; i < n; ++i) {
      int j = i + w;
      if (j < n) {
        for (int k = i + 1; k <= j; ++k) if (a[i][k] and dp[i + 1][k] and dp[j][k]) {
          dp[j][i] = true;
          break;
        }
      }
      j = i - w;
      if (j >= 0) {
        for (int k = i - 1; k >= j; --k) if (a[i][k] and dp[i - 1][k] and dp[j][k]) {
          dp[j][i] = true;
          break;
        }
      }
    }
  }
  int res = 0;
  for (int i = 0; i < n; ++i) res += dp[0][i] and dp[n - 1][i];
  cout << res << '\n';
}

