            int j = i+d;
            DP[j][i] = (A[i] & DP[i+1] & DP[j]).any();
            DP[i][j] = (A[j] & DP[i] & DP[j-1]).any();
        }
    }
    int Res = 0;
    for (int n = 0; n < N; ++n) {
        if (DP[0][n] & DP[N-1][n]) ++Res;
    }
    cout << Res << endl;

    return 0;
}

