				if (e[l][m] && wr[l+1][m] && wl[m][r]) {
					wl[l][r] = 1;
					break; 
				}
			}			
		}
	}

	int ans = 0;
	rep(i, N) if (wl[i][N-1] && wr[0][i]) {
		ans++;
	}
	cout << ans << endl;
	return 0;
}

