long long int extgcd(long long int a,long long int b,long long int &x,long long int &y){
	long long int d=a;
	if(b!=0){
		d=extgcd(b,a%b,y,x);
		y-=(a/b)*x;
	}
	else{
		x=1;
		y=0;
	}
	return d;
}
long long int modinverse(long long int a,long long int b){   //a(mod b)
	long long int x,y;
	long long int d=extgcd(a,b,x,y);
	return (x%b+b)%b;
}

