	const int mod=1e9+7;
	auto add = [&mod](int a, int b) -> int {a += b; return a >= mod? a - mod: a;};
	auto sub = [&mod](int a, int b) -> int {a -= b; return a < 0? a + mod: a;};
	auto mul = [&mod](int a, int b) -> int {return 1LL * a * b % mod;};
	auto qpow = [&](int64_t t,int64_t p) -> int {
		int64_t ans = 1;
		while (p) {
			if(p & 1) ans = ans * t %mod;
			t = t * t % mod;
			p >>=1;
		}
		return ans;
 	};
	int n, a, b, c;
