	vector<int> pre(2 * n + 1), inv(2 * n + 1);
	pre[0] = 1;
	for(int i = 1; i <= 2 * n; ++i) {
		pre[i] = mul(pre[i - 1], i);
		inv[i] = qpow(pre[i], mod-2);
	}
	auto C = [&](int n, int m) {
		return mul(pre[n], mul(inv[m], inv[n-m]));
	};
	int ans = 0;
	int tmp = qpow(qpow(sub(1, c), mod - 2), n);
	for (int i = 0; i <= n; ++i) {
		int tt = mul(C(i + n - 1, n - 1), qpow(b, i));
