char buf[3000];
int main(void) {
  int n = 2000;
  scanf("%d", &n);
  REP(i, n-1) {
    scanf("%s", buf);
    REP(j, i+1) {
      bool b;
      b = buf[j] == '1';
      g[i+1][j] = b;
      g[j][i+1] = !b;
    }
  }
  REP(i, n) {
    g[i][i] = true;
    dp1[i][i] = true;
    dp2[i][i] = true;
  }
  for(int d = 1; d < n; ++d) {
    REP(i, n-d) {
      int j = i + d;
      for(int k = i; k < j; ++k) {
        dp1[i][j] |= g[j][k] & dp1[i][k] & dp2[j-1][k];
      }
      for(int k = i+1; k <= j; ++k) {
        dp2[j][i] |= g[i][k] & dp1[i+1][k] & dp2[j][k];
      }
    }
  }
