			}
			else {
				need_pull[j][cnp[i]++] = i;
				need_upd[i][cnu[i]++] = j;
			}
			f[i][j] = x; f[j][i] = 1-x;
		}
	}

	l=r=0;
	for(int i=1;i<=n;i++) {
		if(i>1&&f[i][i-1]) lef[i] = i-1;
		else lef[i] = i;
		if(i<n&&f[i][i+1]) rig[i] = i+1;
		else rig[i] = i;

		q[r++] = i;
		inq[i] = true;
	}

	int lim = ran*ran-11;
