				upd = true;
			}
			if(cur<nxt && rig[cur] >= lef[nxt]-1 && lef[nxt] > lef[cur]) {
				lef[nxt] = lef[cur];
				upd = true;
			}
			if(upd && !inq[nxt]) {
				q[r++] = nxt;
				if(r==lim) r=0;
