const int MAXN = (1 << 11);

int n;
bitset<MAXN> A[MAXN];

void read()
{
	cin >> n;
	for(int i = 2; i <= n; i++)
	{
		string s;
		cin >> s;
		for(int j = 1; j < i; j++)
		{
			int x = s[j - 1] - '0';
			A[j][i] = x ^ 1;
			A[i][j] = x;
		}
	}
}

bitset<MAXN> lft[MAXN];
bitset<MAXN> rgh[MAXN];
bitset<MAXN> winners[MAXN][MAXN];

void solve()
{
	for(int i = 1; i <= n; i++)
		lft[i][i] = 1, rgh[i][i] = 1, winners[i][i][i] = 1;

	for(int len = 2; len <= n; len++)
	{
		for(int i = 1; i <= n; i++)
		{
			int r = i + len - 1;
			if(r > n) continue;
			bitset<MAXN> qq = A[i] & winners[i + 1][r];
			if(qq.count()) lft[r][i] = 1;
		}

		for(int i = 1; i <= n; i++)
		{
			int l = i - len + 1;
			if(l <= 0) continue;
			bitset<MAXN> qq = A[i] & winners[l][i - 1];
			if(qq.count()) rgh[l][i] = 1;
		}

		for(int l = 1; l <= n; l++)
		{
			int r = l + len - 1;
			if(r > n) continue;;
			winners[l][r] = rgh[l] & lft[r];
		}
	}

	cout << winners[1][n].count() << endl;
}

int main()
{
	ios_base::sync_with_stdio(false);
	cin.tie(NULL);

	read();
	solve();
	return 0;
}

