			Rep1(k, l + 1, r) {
				if (b[l][k] && ri[l + 1][k] && le[r][k]) {
					le[r][l] = true; break;
				}
			}
			Rep(k, l, r) {
				if (b[r][k] && ri[l][k] && le[r - 1][k]) {
					ri[l][r] = true; break;
				}
			}
		}
	}
	rep(i, n) {
		if (ri[0][i] && le[n - 1][i])ans++;
	}
	cout << ans << endl;
}

