                dp[l - 1][r] |= (dp[l][r] & wy[l - 1]);
                if ((dp[l][r] & wx[l - 1]).any()) dp[l - 1][r].set(l - 1);
            }
            if (r < n){
                dp[l][r + 1] |= (dp[l][r] & wy[r]);
                if ((dp[l][r] & wx[r]).any()) dp[l][r + 1].set(r);
            }
        }
    }
    cout << dp[0][n].count() << endl;

    return 0;
}

