        }
    }
    for(unsigned long i = 0; i < N; ++i){
        dp[i] = vector<bitset<maxN>>(N - i);
        dp[i][0][i] = true;
    }
    for(unsigned long i = 1; i < N; ++i){
        for(unsigned long j = 0; j + i < N; ++j){
            if((dp[j + 1][i - 1] & tapi[j]).any())dp[j][i][j] = true;
            if((dp[j][i - 1] & tapi[j + i]).any())dp[j][i][j + i] = true;
            if((dp[j + 1][i - 1] & ~tapi[j]).any())dp[j][i] |= dp[j + 1][i - 1];
            if((dp[j][i - 1] & ~tapi[j + i]).any())dp[j][i] |= dp[j][i - 1];
        }
    }
    cout << dp[0].back().count() << endl;
