short yusho[2521][2521][2];

bool calc(int l, int r, int k){
    if(l>r)return true;
    if(yusho[l][r][k] != -1){
        return yusho[l][r][k]==1;
    }
    int p = k==0 ? l-1 : r+1;
    int to = nxt[p][l];
    while(to <= r){
        bool ok = true;
        if(to>l)ok = ok && calc(l,to-1,1);
        if(to<r)ok = ok && calc(to+1,r,0);
        if(ok){
            return yusho[l][r][k] = 1;
        }
        to = nxt[p][to+1];
    }
    return yusho[l][r][k] = 0;
}

