using namespace std;
const int maxn = 2020;
using bset = bitset<maxn>;
bool dpl[maxn][maxn], dpr[maxn][maxn];
bset beat[maxn], beatl[maxn], beatr[maxn], pl[maxn];
int main(){
	int n; cin >> n;
	for(int i = 1; i < n; i++){
		for(int j = 0; j < i; j++){
			char c; cin >> c;
			if(c == '1') beat[i][j] = 1;
			else beat[j][i] = 1;
		}
	}
	for(int i = 1; i <= n; i++){
		pl[i] = pl[i - 1];
		pl[i].set(i - 1);
	}
	for(int i = 0; i < n; i++) dpl[i][i] = 1, dpr[i][i] = 1, beatl[i][i] = 1, beatr[i][i] = 1;
	for(int i = 2; i <= n; i++){
		for(int j = 0; j + i - 1 < n; j++){
			int from = j, to = j + i - 1;
			bset fr = beatr[from + 1];
			bset bk = beatl[to];
			bset now = beat[from];
			now &= fr;
			now &= bk;
			if(now.any()){
				dpl[from][to] = 1;
				beatl[to][from] = 1;
			}
		}
		for(int j = n - 1; j - i + 1 >= 0; j--){
			int from = j, to = j - i + 1;
			bset fr = beatr[to];
			bset bk = beatl[from - 1];
			bset now = beat[from];
			now &= bk;
			now &= fr;
			if(now.any()){		
				dpr[from][to] = 1;
				beatr[to][from] = 1;
			}
		}
	}
	int ans = 0;
	for(int i = 0; i < n; i++) if(dpr[i][0] && dpl[i][n - 1]) ans++;
	cout << ans << endl;
	return 0;
}

