    }
    dp[1][1] = dp[1][1] = 1;
    for (int k = 1;k<=n-1;k++)
    {
        for (int i = 1;i<=n-k;i++)
        {
            int flag = 0;
            for (int j = i;j<=i+k;j++)
            {
                if (!dp[i][i+k] && j != i && a[i][j] && dp[j][i + k] && dp[j][i+1])
                {
                    dp[i][i+k] = 1;
                    flag++;
                }
                if (!dp[i+k][i] && j != i + k && a[i+k][j] && dp[j][i+k-1] && dp[j][i])
                {
