    int n;
    cin >> n;
    srep(i,1,n){
        string s;
        cin >> s;
        rep(j,i){
            win[i][j] = (s[j] == '1');
            win[j][i] = !win[i][j];
        }
    }
    rep(i,n){
        rep(j,n){
            if(j == i) continue;
            if(!win[i][j]) up[i][j] = 1;
        }
    }
    rep(i,n){
        made[i] = i;
        do {
            if(!win[i][made[i]-1]){
                break;
            }
        }while(--made[i] > 0);
    }
    st[0][0] = 1;
    srep(i,1,n){
        bool flag = false;
        rep(j,i){
            if(st[i-1][j]){
                if(win[i][j]){
                    flag = true;
                    break;
                }
            }
        }
        if(flag){
            st[i][i] = 1;
        }
        int lf = INF;
        rrep(j,i){
            if(win[i][j]){
                cmn(lf, made[j]);
                st[i] |= (st[j] & up[i]);
            }else{
                st[i] |= st[j];
                if(lf > j){
                    break;
                }else{
                    cmn(lf, made[j]);
                }
            }
        }
    }
    cout << (int)st[n-1].count() << "\n";
    return 0;
}

