void solve() {
	cin >> N;
	rep(i, 1, N) {
		string str; cin >> str;
		rep(j, 0, i) {
			A[i][j] = (str[j] == '1');
			A[j][i] = A[i][j] ^ 1;
		}
	}
	rep(i, 0, N) {
		LW[i][i] = 1;
		RW[i][i] = 1;
		LK[i][i] = -1;
		RK[i][i] = -1;
	}
	rep(l, 2, N + 1) {
		rep(i, 0, N - l + 1) {
			int j = i + l - 1;
			// LK
			if(A[i][j] == 1) {
				if(LK[i][j - 1] == -1) LK[i][j] = j;
				else LK[i][j] = (A[j][LK[i][j - 1]] ? j : LK[i][j - 1]);
			}
			else LK[i][j] = LK[i][j - 1];

			// RK
			if(A[j][i] == 1) {
				if(RK[i + 1][j] == -1) RK[i][j] = i;
				else RK[i][j] = (A[i][RK[i + 1][j]] ? i : RK[i + 1][j]);
			}
			else RK[i][j] = RK[i + 1][j];

			// LW
			if(LK[i][j] != -1) {
				int at = LK[i][j];
				LW[i][j] = (RW[i + 1][at] & LW[at][j]);
			}

			// RW
			if(RK[i][j] != -1) {
				int at = RK[i][j];
				// debug("omo", i, j, at);
				RW[i][j] = (RW[i][at] & LW[at][j - 1]);
			}
			// debug(i, j, LK[i][j], RK[i][j], LW[i][j], RW[i][j]);
		}
	}
	int res = 0;
	rep(i, 0, N) {
		res += RW[0][i] & LW[i][N - 1];
	}
	cout << res << "\n";
}

