		cin >> str;
		for (int j = 0; j < i; ++j) {
			if (str[j] == '1') wins[i][j] = 1;
			else wins[j][i] = 1;
		}
	}
	for (int b = 0; b < n; ++b) {
		right_wins[b][b] = 1;
		left_wins[b][b] = 1;
		for (int a = b-1; a >= 0; --a) {
			bitset<N> mask;

			mask = wins[b] & left_wins[a] & right_wins[b-1];
			if (mask.count() > 0) left_wins[a][b] = 1;
			mask = wins[a] & left_wins[a+1] & right_wins[b];
			if (mask.count() > 0) right_wins[b][a] = 1;
		}
	}

	bitset<N> res = left_wins[0] & right_wins[n-1];
	cout << res.count() << '\n';
}

