const int MAX_N = 2000;

int N;
bitset<MAX_N> A[MAX_N];

bitset<MAX_N> dpl[MAX_N], dpr[MAX_N];

ll solve() {
    for (int i = 0; i < N; i++) {
        dpl[i][i] = dpr[i][i] = true;
    }
    for (int l = 1; l < N; l++) {
        for (int i = 0; i + l < N; i++) {
            int j = i + l;
            dpl[j][i] = (A[i] & dpl[j] & dpr[i + 1]).any();
        }
        for (int j = l; j < N; j++) {
            int i = j - l;
            dpr[i][j] = (A[j] & dpr[i] & dpl[j - 1]).any();
        }
    }

    int ans = 0;
    for (int i = 0; i < N; i++) {
        if (dpr[0][i] && dpl[N - 1][i]) {
            ans++;
        }
    }
    return ans;
}

