bool find(int l, int r) {
	if (l == r - 1) return 1;
	if (sv[l][r] != -1) return sv[l][r];

	for (int i = l + 1; i < r; i++) {
		if ((l > 0 && grid[l - 1][i - 1]) || grid[r - 1][i - 1]) {
			bool fl1 = find(l, i);
			if (fl1) {
				if (find(i, r)) {
					sv[l][r] = 1;
					return 1;
				}
			}
		}
	}

	sv[l][r] = 0;
	return 0;
}

