int main() {
	cin >> N;
	for (int i = 0; i < N; i++) {
		for (int j = 0; j < i; j++) {
			char c; cin >> c;
			if (c == '1') { A[i].set(j); A[j].reset(i); }
			else { A[i].reset(j); A[j].set(i); }
		}
