		}
	}
	for (i = n - 1; i >= 0; i--) {
		RR[i][i] = 1;
		if (i == n - 1) {
			Right[i] = 1;
			Left[i] = 1;
			continue;
		}
		Left[i] = 1;
		for (j = i + 1; j < n; j++) {
			if (w[i][j] && Right[j] && Left[j]) {
				Right[i] = 1;
			}
			if (w[i][j]) {
				RR[i] |= RR[j];
			}
		}
		for (j = i + 1; j < n; j++) {
			if (RR[i][j - 1] && w[j][i]) B[j][i] = 1;
		}
		U.reset();
