#include <bits/stdc++.h>
#define REP(i, n) for (int i = 0; (i) < (int)(n); ++ (i))
#define REP3(i, m, n) for (int i = (m); (i) < (int)(n); ++ (i))
#define REP_R(i, n) for (int i = (int)(n) - 1; (i) >= 0; -- (i))
#define REP3R(i, m, n) for (int i = (int)(n) - 1; (i) >= (int)(m); -- (i))
#define ALL(x) begin(x), end(x)
using namespace std;

constexpr int MAX_N = 2000;
int solve(int n, const vector<bitset<MAX_N> > & wins) {
    vector<vector<bitset<MAX_N> > > dp(n + 1);
    REP (r, n + 1) {
        dp[r].resize(r);
    }
    REP_R (l, n) REP3 (r, l + 1, n + 1) {
        if (r - l == 1) {
            dp[r][l][l] = true;
        } else {
            dp[r][l][l] = (dp[r][l + 1] & wins[l]).any();
            REP3 (m, l + 1, r - 1) {
                dp[r][l][m] = (dp[m][l] & wins[m]).any() and (dp[r][m + 1] & wins[m]).any();
            }
            dp[r][l][r - 1] = (dp[r - 1][l] & wins[r - 1]).any();
        }
    }
    return dp[n][0].count();
}

int main() {
    int n; cin >> n;
    assert (n <= MAX_N);
    vector<bitset<MAX_N> > wins(n);
    REP (i, n) {
        REP (j, i) {
            char c; cin >> c;
            wins[i][j] = c - '0';
            wins[j][i] = not wins[i][j];
        }
    }
    cout << solve(n, wins) << endl;
    return 0;
}

