  int n; cin >> n;
  V< bitset<2000> > a(n);
  for (int i = 1; i < n; ++i) {
    string s; cin >> s;
    for (int j = 0; j < i; ++j) {
      a[i][j] = s[j] == '1';
      a[j][i] = s[j] == '0';
    }
  }
  V< bitset<2000> > dp(n);
  for (int i = 0; i < n; ++i) {
    dp[i][i] = true;
    if (i) dp[i][i - 1] = a[i][i - 1];
    if (i + 1 < n) dp[i][i + 1] = a[i][i + 1];
  }
  for (int w = 2; w < n; ++w) {
    for (int i = 0; i < n; ++i) {
      int j = i + w;
      if (j < n) {
        for (int k = i + 1; k <= j; ++k) if (a[i][k] and dp[k][i + 1] and dp[k][j]) {
          dp[i][j] = true;
          break;
        }
      }
      j = i - w;
      if (j >= 0) {
        for (int k = i - 1; k >= j; --k) if (a[i][k] and dp[k][i - 1] and dp[k][j]) {
          dp[i][j] = true;
          break;
        }
      }
    }
  }
  int res = 0;
  for (int i = 0; i < n; ++i) res += dp[i][0] and dp[i][n - 1];
  cout << res << '\n';
}

