int main(int argc, const char * argv[]) {
    ios_base::sync_with_stdio(false);
    int N;
    cin >> N;
    bitset<2020> A[2020];
    char acBuf[2020];
    for (int n = 1; n < N; ++n) {
        scanf("%s", acBuf);
        for (int m = 0; m < n; ++m) {
            A[n][m] = acBuf[m]-'0';
        }
    }

    bitset<2020> DP[2020];
    for (int n = 0; n < N; ++n) {
        DP[n][n] = 1;
    }
    for (int d = 1; d <= N-1; ++d) {
        for (int i = 0; i+d < N; ++i) {
            int j = i+d;
            bool b = false;
            for (int k = i+1; k <= j; ++k) {
                b |= !A[k][i] & DP[k][i+1] & DP[k][j];
            }
            DP[i][j] = b;

            b = false;
            for (int k = i; k < j; ++k) {
                b |= A[j][k] & DP[k][i] & DP[k][j-1];
            }
            DP[j][i] = b;
        }
    }
    int Res = 0;
    for (int n = 0; n < N; ++n) {
        if (DP[n][0] & DP[n][N-1]) ++Res;
    }
    cout << Res << endl;

    return 0;
}

