int n;
char p[2100][2100];
int w[2100][2100], Right[2100], Left[2100], TLeft[2100];
bitset<2100>B[2100], U, RR[2100];
int main() {
	int i, j;
	scanf("%d", &n);
	for (i = 1; i < n; i++) {
		scanf("%s", p[i]);
		for (j = 0; j < i; j++) {
			if (p[i][j] == '1') {
				w[i][j] = 1;
			}
			else w[j][i] = 1;
		}
	}
	for (i = n - 1; i >= 0; i--) {
		RR[i][i] = 1;
		if (i == n - 1) {
			Right[i] = 1;
			Left[i] = 1;
			continue;
		}
		Left[i] = 1;
		for (j = i + 1; j < n; j++) {
			if (w[i][j] && Right[j] && Left[j]) {
				Right[i] = 1;
			}
			if (w[i][j] && Left[j]) {
				RR[i] |= RR[j];
			}
		}
		for (j = i + 1; j < n; j++) {
			if (RR[i][j - 1] && w[j][i]) B[j][i] = 1;
		}
		U.reset();
		U[i] = 1;
		for (j = i + 1; j < n; j++) {
			if ((U & B[j]).count()) {
				Left[j] = 1;
				U[j] = 1;
			}
			else {
				Left[j] = 0;
			}
		}
	}
	int res = 0;
	for (i = 0; i < n; i++) {
		if (Right[i] && Left[i])res++;
	}
	printf("%d\n", res);
}

