const int MAX = 10;

int n;
bitset<MAX> pref[MAX], suf[MAX], win[MAX];
int a[MAX][MAX];

void solve () {
	fi(0, n - 1) {
		fj(0, n - 1) {
			if (a[i][j]) win[i].set(j);
		}
	}
	dbg(win[0]);
	fi(0, n - 1) {
		pref[i].set(i);
		suf[i].set(i);						
		fdj(i - 1, 0) {
			if (a[i][j]) {				
				if ( ( (suf[j] << 1) & pref[i]).count() ) {
					pref[i] |= pref[j];
				}
			}
		}
		bitset<MAX> sufwin;
		sufwin.reset();
		sufwin.set(i);
		fdj(i - 1, 0) {
			if ( ((suf[j] << 1) & sufwin & win[j]).count() ) {
				sufwin.set(j);
				suf[j].set(i);
			}
		}
//
		fj(0, i) {
			dbg(pref[j]);
			dbg(suf[j]);
		}
		err("after i = %d\n", i);
//
	}
	vi answer;
	fi(0, n - 1) {
		if (pref[i].test(0) && suf[i].test(n - 1)) {
			answer.pb(i);
		}
	}
	printf("%d\n", sz(answer));
//	dbg(answer);	
}

