    for(int c1 = 0; c1 < n; c1++){
        bitset<MAXN> temp1;
        bitset<MAXN> temp2;
        bitset<MAXN> temp3;
        bitset<MAXN> temp4;
        BR.push_back(temp1);
        BL.push_back(temp2);
        WINR.push_back(temp3);
        WINL.push_back(temp4);
        if(c1 < n-1)cin >> s;
        for(int c2 = 0; c2 < c1; c2++){
          //  cin >> a;
            a = s[c2]-'a';
            win[c1][c2] = a;
            win[c2][c1] = (a^1);
        }
        win[c1][c1] = 1;
    }

    for(int c1 = 0; c1 < n; c1++){

        for(int c2 = c1; c2 < n; c2++){
            WINR[c1][c2] = win[c1][c2];
        }
        for(int c2 = 0; c2 < c1; c2++){
            WINR[c1][c2] = win[c1][c2];
        }
        BR[c1][c1] = 1;
        BL[c1][c1] = 1;
        DPL[c1][c1] = 1;
        DPR[c1][c1] = 1;
    }

    for(int L = 2; L <= n; L++){
        bitset<MAXN> LB;
        for(int c1 = 0; c1 < L; c1++){
            LB[c1] = 1;
        }
        for(int c1 = 0; c1 < n-L; c1++){

            DPL[c1][c1+L-1] = ((WINR[c1] & BL[c1+L-1] & BR[c1]).count() != 0);
            DPR[c1][c1+L-1]= ((WINL[c1+L-1] & BL[c1+L-1] & BR[c1]).count() != 0);
            (LB <<= 1);
        }

        for(int c1 = 0; c1 < n-L; c1++){
            BR[c1][c1+L-1] = DPR[c1][c1+L-1];
            BL[c1][c1+L-1] = DPL[c1][c1+L-1];
        }
    }
    ll ans = 0;

    for(int c1 = 0; c1 < n; c1++){
        if(DPL[c1][n-1]&DPR[0][c1])ans++;
    }
    cout << ans << "\n";
    return 0;
}

