bitset < 2048 > ok[2048];
bitset < 2048 > d[2][2048];
bitset < 2048 > was[2][2048];
int nxt1[2048][2048];
int nxt2[2048][2048];
int used = 0;
bool f(int tp,int l,int r) {
	if (was[tp][l][r])
		return d[tp][l][r];
	if (l >= r)
		return 1;
	++used;
	was[tp][l][r] = 1;
	bool ans = 0;
	if (!tp) {
		for (int i = nxt1[l][l];!ans && i <= r;i = nxt1[l][i + 1])
			ans |= f(1,l + 1,i) && f(0,i,r);
	} else {
		for (int i = nxt2[r][r];!ans && i >= l;i = nxt2[r][i - 1])
			ans |= f(1,l,i) && f(0,i,r - 1);
	}
	return d[tp][l][r] = ans;
}
