    		nxt1[i][j] = ok[i][j] ? j : nxt1[i][j + 1];
    	}
    	nxt2[i][0] = 0;
    	for (int j = 1;j <= n;++j) {
    		nxt2[i][j] = ok[i][j] ? j : nxt2[i][j - 1];
    	}
    }
    int ans = 0;
