//Push-Relabel Algorithm for Flows - Gap Heuristic, Complexity: O(V^3)
//To obtain the actual flow values, look at all edges with capacity > 0
//Zero capacity edges are residual edges
struct edge{
	int from, to, cap, flow, index;
	edge(int from, int to, int cap, int flow, int index):
		from(from), to(to), cap(cap), flow(flow), index(index) {}
};
struct PushRelabel{
	int n;
	vector<vector<edge> > g;
	vector<long long> excess;
	vector<int> height, active, count;
	queue<int> Q;
	PushRelabel(int n):
		n(n), g(n), excess(n), height(n), active(n), count(2*n) {}
	void addEdge(int from, int to, int cap){
		dbg(from,to);
		g[from].push_back(edge(from, to, cap, 0, g[to].size()));
		if(from==to)
			g[from].back().index++;
		g[to].push_back(edge(to, from, 0, 0, g[from].size()-1));
	}
	void enqueue(int v){
		if(!active[v] && excess[v] > 0)
		{
			active[v]=true;
			Q.push(v);
		}
	}
	void push(edge &e){
		int amt=(int)min(excess[e.from], (long long)e.cap - e.flow);
		if(height[e.from]<=height[e.to] || amt==0)
			return;
		e.flow += amt;
		g[e.to][e.index].flow -= amt;
		excess[e.to] += amt;
		excess[e.from] -= amt;
		enqueue(e.to);
	}
	void relabel(int v){
		count[height[v]]--;
		int d=2*n;
		for(auto &it:g[v]){
			if(it.cap-it.flow>0)
				d=min(d, height[it.to]+1);
		}
		height[v]=d;
		count[height[v]]++;
		enqueue(v);
	}
	void gap(int k){
		for(int v=0;v<n;v++){
			if(height[v]<k)
				continue;
			count[height[v]]--;
			height[v]=max(height[v], n+1);
			count[height[v]]++;
			enqueue(v);
		}
	}
	void discharge(int v){
		for(int i=0; excess[v]>0 && i<g[v].size(); i++)
			push(g[v][i]);
		if(excess[v]>0){
			if(count[height[v]]==1)
				gap(height[v]);
			else
				relabel(v);
		}
	}
	long long max_flow(int source, int dest){
		count[0] = n-1;
		count[n] = 1;
		height[source] = n;
		active[source] = active[dest] = 1;
		for(auto &it:g[source]){
			excess[source]+=it.cap;
			push(it);
		}
		while(!Q.empty()){
			int v=Q.front();
			Q.pop();
			active[v]=false;
			discharge(v);
		}
		long long max_flow=0;
		for(auto &e:g[source])
			max_flow+=e.flow;

		return max_flow;
	}
};
bool find(int x){
	int s=x,t=n;
	PushRelabel f(n+1);
	forn(i,n){
		forn(j,i){
			if(mat[i][j]){
				// dbg(i,j);
				if(j==x) continue;
				else f.addEdge(i,j,2005);
			}else{
				if(i==x) continue;
				else f.addEdge(j,i,2005);
			}
		}
		f.addEdge(i,t,1);
	}
	auto ans=f.max_flow(s,t);
	if(ans==n) return 1;
	else return 0;
}
