	}
	vec.pb(a);
	vec.pb(b);
	sort(all(vec));
	vec.erase(unique(all(vec)),vec.end());
	if(vec.size() == 2) return;
	for(int i = 0;i < vec.size() - 1;i++) calc(vec[i],vec[i + 1]);
}

