LL modinv(LL a){
    LL b = MOD, u = 1, v = 0;
    while(b){
        LL t = a / b;
        a -= t * b;swap(a, b);
        u -= t * v;swap(u, v);
    }
    u%=MOD;
    if(u < 0) u += MOD;
    return u;

}
bool checker(int a, int b, vector<vector<int> > &win){
    bool flag = a < b;
    if(a<b)swap(a, b);
    return flag != win[a][b];
}
int main(){
    int N;
    cin >> N;
    //challenger adsf;
    vector<vector<int> > win(N+3, vector<int>(N+3, 0));
    vector<int> check(N, -1);
    REP(i, N-1){
        REP(j, i+1){
            char d;
            cin >> d;
            win[i+1][j] = d-'0';
        }
    }
    int ans = 0;
    REP(i, N){
        int count = 0;
        queue<int> que;
        REP(j, N){
            if(i==j)continue;
            if(checker(i, j, win)){
                ++count;
                check[j]=i;
                que.push(j);
            }
        }
        while(!que.empty()){
            int d = que.front();
            que.pop();
            REP(j, N){
                if(j==i)continue;
                if(check[j]==i)continue;
                int a = d;
                int b = j;
                if(a==b)continue;
                if(a<b)swap(a, b);
                if(a > i && i > b)continue;
                if(checker(d, j, win)){
                    ++count;
                    check[j]=i;
                    que.push(j);
                }
            }
        }
        if(count + 1==N){
            ++ans;
        }
    }
    cout << ans << endl;
    return 0;
}

