            for(int k=j+1;k<=R && !j1;k++){
                j1|=A[j][k] & dp[l][k] & dp2[R][k];
            }
            dp2[R][j]=j1;
        }
        for(int j=i;j<n;j++){
            int R=j-i;
            int l=j-1;
            bool j1=false;
            for(int k=R;k<j && !j1;k++){
                j1|=A[j][k] & dp2[l][k] & dp[R][k];
            }
            dp[R][j]=j1;
        }
    }
