int dp[N][N]; // left win
bool A[N][N];
int n;

// left win
int rec(int a, int b){
  if(dp[a][b] != -1) return dp[a][b];
  if(a < b) for(int i = a + 1; i <= b; i++){
    if(A[a][i] & rec(i, b)){
      for(int j = a; j < i; j++){
        if(rec(a, j) & rec(i, j+1)){
          return dp[a][b] = 1;
        }
      }
    }
  }
  else for(int i = a - 1; i >= b; i--){
    if(A[a][i] & rec(i, b)){
      for(int j = a; j > i; j--){
        if(rec(a, j) & rec(i, j-1)){
          return dp[a][b] = 1;
        }
      }
    }
  }
  return dp[a][b] = 0;
}

