using namespace std;

const int max_n = 2022, inf = 1000111222;

char buf[max_n];
bitset<max_n> a[max_n], dp0[max_n], dp1[max_n], dp2[max_n];

int n;

int main() {
    //freopen("input.txt", "r", stdin);
    //freopen("output.txt", "w", stdout);
    scanf("%d", &n);
    for (int i = 1; i < n; ++i) {
        scanf("%s", buf);
        for (int j = 0; j < i; ++j) {
            a[i][j] = buf[j] - '0';
            a[j][i] = 1 ^ a[i][j];
        }
    }
    for (int i = 0; i < n; ++i) {
        dp0[i][i] = 1;
        dp1[i][i] = 1;
        dp2[i][i] = 1;
    }
    for (int len = 2; len <= n; ++len) {
        for (int l = 0; l + len <= n; ++l) {
            int r = l + len - 1;
            if (len == 2) {
                dp2[l][r] = 1;
            } else {
                dp2[l][r] = (dp2[l] & dp2[r] & (a[l] | a[r])).any();
            }
            dp2[r][l] = dp2[l][r];

            if (a[l][r] && dp2[l][r]) {
                dp0[l][r] = 1;
            } else {
                dp0[l][r] = (a[l] & dp0[l] & dp0[r]).any();
            }
            dp0[r][l] = dp0[l][r];

            if (a[r][l] && dp2[l][r]) {
                dp1[l][r] = 1;
            } else {
                dp1[l][r] = (a[r] & dp1[l] & dp1[r]).any();
            }
            dp1[r][l] = dp1[l][r];
        }
    }
    int ans = 0;
    for (int i = 0; i < n; ++i) {
        bool ok = dp1[0][i] & dp0[i][n - 1];
        ans += ok;
    }
    printf("%d\n", ans);
    return 0;
}

